/* USER CODE BEGIN 4 */
void EXTI0_IRQHandler(void){
	// TODO: Debounce using HAL_GetTick()
    uint32_t now = HAL_GetTick();
    if (now - lastButtonPress > 200) { // 200ms debounce
        lastButtonPress = now;

    	// TODO: Disable DMA transfer and abort IT, then start DMA in IT mode with new LUT and re-enable transfer
        __HAL_TIM_DISABLE_DMA(&htim2, TIM_DMA_CC1);
        HAL_DMA_Abort_IT(&hdma_tim2_ch1);

        // Move to next waveform
        currentWaveform = (currentWaveform + 1) % 6;

        // Start DMA with new LUT
        HAL_DMA_Start_IT(&hdma_tim2_ch1, (uint32_t)waveforms[currentWaveform], DestAddress, NS);
        __HAL_TIM_ENABLE_DMA(&htim2, TIM_DMA_CC1);

        // Update LCD
        lcd_command(CLEAR);
        lcd_command(CURSOR_HOME);
        lcd_putstring(waveformNames[currentWaveform]);

    }

    HAL_GPIO_EXTI_IRQHandler(Button0_Pin); // Clear interrupt flag
}
